//
// Represents the UI of a Cosmac Elf computer.
//

var CosmacElfUIObj = function() {
	
	// the emulator object
	this.emu = new CosmacElfObj(this);
	
	// references to HTML elements, see createUI()
	this.ctx = null;
	this.offImg = null;
	this.onImg = null;
	this.ledOnImg = null;
	this.hexImg = null;

	// dimensions of the HTML5 canvas
	this.canvasWidth = 452;
	this.canvasHeight = 415;
	
	// location and dimension of switches, display, and LED
	this.switchX = [ 351, 307, 264, 222, 180, 138,  95,  52, 350, 307, 100,  65 ];
	this.switchY = [ 333, 333, 333, 333, 333, 333, 333, 333, 268, 268, 268, 305 ];
	this.switchOnY = [ 65, 65, 65, 65, 65, 65, 65, 65, 0, 0, 0, 37 ];
	this.switchW = [ 42, 44, 43, 42, 42, 42, 43, 43, 40, 41, 40, 28 ];
	this.switchH = [ 66, 66, 66, 66, 66, 66, 66, 66, 65, 65, 65, 26 ];
	this.switchDirty = [ false, false, false, false, false, false, false, false, false, false, false, false ];

	this.display1X = 233;
	this.display2X = 205;
	this.displayY = 222;
	this.displayW = 17;
	this.displayH = 23;
	this.displayDirty = false;

	this.ledX = 222;
	this.ledY = 175;
	this.ledW = 13;
	this.ledH = 17;
	this.ledDirty = false;

	// popup with credits appears above this line
	this.popupY = 265;
	
	// key map binding keys to switches
	// get event.which scancodes from http://api.jquery.com/keydown/
	this.inButtonKey = 32;   // space -> in
	this.keyMap = {
		186: 0, // ; -> switch 0 (for IE/chrome)
		59: 0,  // ;             (for mozilla)
		76: 1,  // L -> switch 1
		75: 2,  // K -> switch 2
		74: 3,  // J -> switch 3
		70: 4,  // F -> switch 4
		68: 5,  // D -> switch 5
		83: 6,  // S -> switch 6
		65: 7,  // A -> switch 7
		13: 8,  // ENTER -> run
		72: 9,  // H -> mp
		71: 10  // G -> load
	};
};

// creates and initialises the required HTML elements
CosmacElfUIObj.prototype.createUI = function(canvasId) {
	this.offImg = $("<img src='data:image/png;base64,' alt='Cosmac Elf with switches off' style='display:none;' />")
			.appendTo("body")[0];
	this.onImg = $("<img src='data:image/png;base64,' alt='Cosmac Elf with switches on' style='display:none;' />")
			.appendTo("body")[0];
	this.ledOnImg = $("<img src='data:image/png;base64,' alt='All digits of the hex display' style='display:none;' />")
			.appendTo("body")[0];
	this.hexImg = $("<img src='data:image/png;base64,' alt='Cosmac Elf LED in on state' style='display:none;' />")
			.appendTo("body")[0];

	var c = $(canvasId);
	c.width(this.canvasWidth).height(this.canvasHeight);
	this.ctx = c[0].getContext('2d');
	
	// install event handlers
	this.installEventHandlers(c);
	
	// draw the background image with a 500ms delay to ensure images are loaded
	var elfUI = this;
	setTimeout(function() {
		elfUI.ctx.drawImage(elfUI.offImg, 0, 0);
	}, 500);
}

// starts the emulator
CosmacElfUIObj.prototype.start = function() {
	this.emu.emulate();
}

// update user interface
CosmacElfUIObj.prototype.draw = function() {
	for (var i=0; i<this.switchDirty.length; i++) {
		if (this.switchDirty[i]) {
			this.switchDirty[i] = false;
			
			if (this.emu.switchState[i]) {
				this.ctx.drawImage(this.onImg,
						this.switchX[i], this.switchOnY[i], this.switchW[i], this.switchH[i], 
						this.switchX[i], this.switchY[i], this.switchW[i], this.switchH[i]);
			} else {
				this.ctx.drawImage(this.offImg,
						this.switchX[i], this.switchY[i], this.switchW[i], this.switchH[i], 
						this.switchX[i], this.switchY[i], this.switchW[i], this.switchH[i]);
			}
		}
	}
	
	if (this.displayDirty) {
		this.displayDirty = false;

		var n = this.emu.display & 0x0f;
		var i = this.emu.display >> 4;

		this.ctx.drawImage(this.hexImg, n * this.displayW, 0, this.displayW, this.displayH,
				this.display1X, this.displayY, this.displayW, this.displayH);
		this.ctx.drawImage(this.hexImg, i * this.displayW, 0, this.displayW, this.displayH,
				this.display2X, this.displayY, this.displayW, this.displayH);
	}
	
	if (this.ledDirty) {
		this.ledDirty = false;
	
		if (this.emu.led) {
			this.ctx.drawImage(this.ledOnImg, 0, 0, this.ledW, this.ledH,
					this.ledX, this.ledY, this.ledW, this.ledH);
		} else {
			this.ctx.drawImage(this.offImg, this.ledX, this.ledY, this.ledW, this.ledH,
					this.ledX, this.ledY, this.ledW, this.ledH);
		}
	}
}

// install event handlers
CosmacElfUIObj.prototype.installEventHandlers = function(canvas) {
	var elfUI = this;
	$(document).keydown(function(event) {
		if (event.which == elfUI.inButtonKey) { // IN key down
			elfUI.switchDirty[11] = true;
			elfUI.emu.inButtonDown();
			return false;
		}
	})
	.keyup(function(event) {
		// change state of buttons and set to dirty
		if (event.which == elfUI.inButtonKey) { // IN button
			elfUI.switchDirty[11] = true;
			elfUI.emu.inButtonUp();
			return false;
		} else if (event.which in elfUI.keyMap) { // key defined in keyMap
			var idx = elfUI.keyMap[event.which];
			elfUI.switchDirty[idx] = true;
			elfUI.emu.toggleSwitchState(idx);
			return false;
		}
		return true;
	});
	canvas.mousedown(function(event) {
		var x = event.pageX - this.offsetLeft;
		var y = event.pageY - this.offsetTop;
		if (x > elfUI.switchX[11] && x < elfUI.switchX[11]+elfUI.switchW[11] // IN button
				&& y > elfUI.switchY[11] && y < elfUI.switchY[11]+elfUI.switchH[11]) {
			elfUI.switchDirty[11] = true;
			elfUI.emu.inButtonDown();
		}
		return false;
	})
	.mouseup(function(event) {
		var x = event.pageX - this.offsetLeft;
		var y = event.pageY - this.offsetTop;

		// find out which button/switch was clicked
		for (var i=0; i<elfUI.switchX.length; i++) {
			if (x > elfUI.switchX[i] && x < elfUI.switchX[i]+elfUI.switchW[i]
					&& y > elfUI.switchY[i] && y < elfUI.switchY[i]+elfUI.switchH[i]) {
				// toggle the button/switch
				if (i == 11) { // IN button
					elfUI.switchDirty[11] = true;
					elfUI.emu.inButtonUp();
				} else { // key defined in keyMap
					elfUI.switchDirty[i] = true;
					elfUI.emu.toggleSwitchState(i);
				}
				return false;
			}
		}
		
		if (y < elfUI.popupY) {
			alert("cosmacelf.js\n\nCopyright (2013) by Barend Scholtus\n\nGraphics by Bill Richman and Chris Radek");
		}
		
		return false;
	});
}

// draw outlines of each switch/button
// for debugging of drawing and mouse events
CosmacElfUIObj.prototype.drawButtonOutlines = function() {
	for (var i=0; i<this.switchX.length; i++) {
		this.ctx.beginPath();
		this.ctx.strokeStyle = '#f00';
		this.ctx.moveTo(this.switchX[i], this.switchY[i]);
		this.ctx.lineTo(this.switchX[i]+this.switchW[i], this.switchY[i]);
		this.ctx.lineTo(this.switchX[i]+this.switchW[i], this.switchY[i]+this.switchH[i]);
		this.ctx.lineTo(this.switchX[i], this.switchY[i]+this.switchH[i]);
		this.ctx.lineTo(this.switchX[i], this.switchY[i]);
		this.ctx.stroke();
	}
}
